package exam;

public class Ex03_PrintExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String strIs = "는";
		boolean blTrue = true;
		String result = "이다";
		boolean blFalse = false;
		
		
		System.out.println("푸바오" + strIs + " 자이언트 판다" + result + "[" + blTrue + "]");
		System.out.println("원숭이 엉덩이" + strIs + " 노랗다 [" + blFalse + "]");
		System.out.println("나" + strIs + " 여자다 [" + blTrue + "]");
		System.out.println("나" + strIs + " 열살" + result + " [" + blFalse + "]");
		System.out.println("사람의 다리" + strIs + " 3개다 [" + blFalse + "]");
		System.out.println("현재 시간은 오전 9시" + result + " [" + blFalse + "]");
		System.out.println("나" + strIs + " 프로그래밍이 재밌다 [" + blFalse + "]");

	}

}
